import React, { useState, useEffect } from 'react'
import { Text, View, StyleSheet, StatusBar, Platform } from 'react-native'
import { IconButton } from 'react-native-paper';
import { Button } from 'react-native-paper'
import { BarCodeScanner } from 'expo-barcode-scanner'

import { Meal } from '../screens/homescreen';

interface Props {
    setShowBarCodeScanner: React.Dispatch<React.SetStateAction<boolean>>,
    setMeal: React.Dispatch<React.SetStateAction<Meal>>,
}

const BarCodeScannerComponent: React.FC<Props> = ({setShowBarCodeScanner}) => {
    const [hasPermission, setHasPermission] = useState(null);
    const [scanned, setScanned] = useState(false);

    useEffect(() => {
    (async () => {
        const { status } = await BarCodeScanner.requestPermissionsAsync();
        setHasPermission(status === 'granted');
    })();
    }, []);

    if (hasPermission === null) {
        return <Text>Requesting for camera permission</Text>;
    }
    if (hasPermission === false) {
        return <Text>No access to camera</Text>;
    }

    const fetchFoodData = async () => {
        try {
            const response:any = await fetch(`https://world.openfoodfacts.org/api/v0/product/${data}.json`);
            const data = response.json();
            if(data.status == 0) {
                alert(`Product not found. Try again or type in macronutrients manually.`);
            }
        }
        catch (error) {
            alert(error);
        }
    }

    const handleBarCodeScanned = ({ type, data }) => {
    setScanned(true);
    setShowBarCodeScanner(false);
    alert(`Bar code with type ${type} and data ${data} has been scanned!`);
    };

    return (
    <View style={styles.container}>
        <BarCodeScanner
            onBarCodeScanned={scanned ? undefined : handleBarCodeScanned}
            style={StyleSheet.absoluteFillObject}
        />
        {scanned && 
        <Button 
            style={styles.AndroidSafeArea} 
            onPress={() => setScanned(false)}>
                Tap to Scan Again
        </Button>}
        <IconButton 
            style={{alignSelf:'flex-end'}}
            icon='window-close' 
            onPress={() => {
                setShowBarCodeScanner(false);
                console.log('Scanner Closed');
            }}
        />
    </View>
    );
};

export default BarCodeScannerComponent;

const styles = StyleSheet.create({
    container:{
        flex:1,
    },
    AndroidSafeArea: {
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
        flexDirection: 'row',
        justifyContent: 'center',
        backgroundColor: 'darkviolet',
}
})
