import React, { useState, useEffect } from 'react'
import { View, StyleSheet, Modal } from 'react-native'
import { IconButton, Text } from 'react-native-paper';

import DatePickerComponent from '../components/datepicker'
import MealListItemComponent from '../components/meallistitem'
import BottomBarComponent  from '../components/bottombar'
import BarCodeScannerComponent from '../components/barcodescanner'
import AddMealComponent from '../components/addmeal';


interface Meal {
    name: string,
    weight: number,
    carbs: number,
    fat: number,
    protein: number
}

const HomeScreen = () => {
    const [showBarCodeScanner, setShowBarCodeScanner] = useState<boolean>(false);
    const [date, setDate] = useState<Date>(new Date());
    const [meals, setMeals] = useState<Array<Meal>>([
        {
            name: 'French Fries',
            weight: 200,
            carbs: 50,
            fat: 30,
            protein: 10,
        },
        {
            name: 'Hamburger',
            weight: 500,
            carbs: 30,
            fat: 60,
            protein: 40,
        },
    ]);

    useEffect(() => console.log(showBarCodeScanner), [showBarCodeScanner]);

    const setShowScanner = () => {
        // This function exists only
        // because typescript complained about 
        // passing setShowBarCodeScanner as
        // prop
        setShowBarCodeScanner(false);
        console.log('fn worked');
    };

    return (
        <View style={styles.container}>
            <DatePickerComponent date={date} setDate={setDate}/>
            <MealListItemComponent meals={meals}/>
            <AddMealComponent />
            <View style={styles.bottom}>
                <View style={styles.buttons}>
                    <IconButton 
                        onPress={() => setShowBarCodeScanner(true)}
                        icon="plus-circle"
                        color={'darkviolet'}
                        size={60}
                        />
                    <IconButton 
                        icon="note-plus"
                        color={'darkviolet'}
                        size={60}
                        />
                </View>
                <BottomBarComponent />
                    {showBarCodeScanner && 
                    <Modal>
                        <BarCodeScannerComponent 
                            setShowScanner={setShowScanner}/>
                    </Modal>}
            </View>
        </View>
    );
}

export default HomeScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    bottom: {
        position: 'absolute',
        left: 0,
        right: 0,
        bottom: 0,
    },
    buttons: {
        display: 'flex',
        flexDirection: 'row-reverse',
    }
});
