import React, { useState } from 'react'
import { View, StyleSheet, Modal, TextInput } from 'react-native'
import { IconButton } from 'react-native-paper';


export interface Props {
    note: string,
    setNote: React.Dispatch<React.SetStateAction<string>>,
    setShowAddNote: React.Dispatch<React.SetStateAction<boolean>>
}

const AddNoteComponent: React.FC<Props> = ({note, setNote, setShowAddNote}) => {
    const [editedNote, setEditedNote] = useState('');

    return (
        <Modal transparent visible={true}>
            <View style={styles.container}>
                <View style={styles.card}>
                    <View style={styles.input}>
                        <TextInput 
                            multiline
                            placeholder="Write your note for today here."
                            value={editedNote}
                            onChange={(input) => setEditedNote(String(input))}
                        />
                    </View>
                    <View style={styles.buttons}>
                        <IconButton 
                            icon='check'
                            color='darkviolet'
                        />
                        <IconButton 
                            icon='close'
                            color='darkviolet'
                            onPress={() => {
                                setShowAddNote(false);
                            }}
                        />
                    </View>
                </View>
            </View>
        </Modal>
    );
}
export default AddNoteComponent;

const styles = StyleSheet.create({
    container: {
        flex:1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'rgba(0,0,0,0.5)',
    },
    card: {
        display:'flex',
        justifyContent:'center',
        alignItems: 'center',
        backgroundColor: 'white',
        padding: 20,
        borderRadius: 5,
    },
    input: {
        borderBottomColor: 'darkviolet',
        borderBottomWidth: 1,
    },
    buttons: {
        alignSelf:'stretch', 
        flexDirection:'row', 
        justifyContent:'space-between',
        marginTop: 10
    }
})
