import React, { useState, useEffect } from 'react'
import { View, StyleSheet, Modal, TextInput } from 'react-native'
import { Button, IconButton, Text } from 'react-native-paper';

import BarCodeScannerComponent from './barcodescanner';


interface Props {
    setShowAddMeal: (value:React.SetStateAction<boolean>) => void,
}

const AddMealComponent: React.FC<Props> = (props) => {
    const [showBarCodeScanner, setShowBarCodeScanner] = useState<boolean>(false);
    const [text, setText] = React.useState("");

    const setShowScanner = () => {
        // This function exists only
        // because typescript complained about 
        // passing setShowBarCodeScanner as
        // prop
        setShowBarCodeScanner(false);
        console.log('fn worked');
    };


    return(
        <Modal transparent visible={true}>
            <View style={styles.container}>
                <View style={styles.card}>
                    <IconButton 
                        style={{alignSelf:'flex-end'}}
                        icon='window-close' 
                        onPress={() => props.setShowAddMeal}
                    />
                    <View>
                    <TextInput
                        style={styles.input}
                        onChangeText={(input) => setText(input)}
                        value={text}
                        placeholder='Meal Name'
                    />
                    <TextInput
                        style={styles.input}
                        onChangeText={(input) => setText(input)}
                        value={text}
                        placeholder='Weight (g)'
                        keyboardType='numeric'
                    />
                    <TextInput
                        style={styles.input}
                        onChangeText={(input) => setText(input)}
                        value={text}
                        placeholder='Carbs (g per 100g)'
                        keyboardType='numeric'
                    />
                    <TextInput
                        style={styles.input}
                        onChangeText={(input) => setText(input)}
                        value={text}
                        placeholder='Fat (g per 100g)'
                        keyboardType='numeric'
                    />
                    <TextInput
                        style={styles.input}
                        onChangeText={(input) => setText(input)}
                        value={text}
                        placeholder='Protein (g per 100g)'
                        keyboardType='numeric'
                    />
                    </View>
                    <View style={{ flexDirection:'row' ,justifyContent:'center', marginTop:60}}>
                        <View style={styles.calorieCount}>
                            <Text style={{ fontWeight:'bold' }}>Calories in the meal</Text>
                            <Text> 231 kcal</Text>
                        </View>
                        <Button 
                            style={styles.flexBttn} 
                            color='darkviolet' 
                            mode='contained'>
                                Scan
                        </Button>
                        <Button 
                            style={styles.flexBttn} 
                            color='darkviolet' 
                            mode='contained'>
                                Add Meal
                        </Button>
                    </View>
                </View>
            </View>
            {showBarCodeScanner && 
                    <Modal>
                        <BarCodeScannerComponent 
                            setShowScanner={setShowScanner}/>
                    </Modal>}
        </Modal>
    )
}

const styles = StyleSheet.create({
    container: {
        flex:1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'rgba(0,0,0,0.5)',
    },
    card: {
        display:'flex',
        justifyContent:'center',
        alignItems: 'center',
        backgroundColor: 'white',
        padding: 20,
        borderRadius: 5,
    },
    input: {
        height: 40,
        width: 200,
        margin: 12,
        borderBottomWidth: 1.3,
        borderColor:'darkviolet',
        padding: 10,
    },
    flexBttn: {
        marginRight: 20,
    },
    calorieCount: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        marginHorizontal:10,
    }
})


export default AddMealComponent
