import React, { useState, useEffect } from 'react'
import { View, StyleSheet, Modal } from 'react-native'
import { Button, TextInput, Text } from 'react-native-paper';

const AddMealComponent = () => {
    const [text, setText] = React.useState("");
    return(
        <Modal transparent visible={true}>
            <View style={styles.container}>
                <View style={styles.card}>
                    <View style={{flex: 1}}>
                        <View style={{flex: 1, flexDirection: 'row'}}>
                        <TextInput 
                            mode='flat'
                            style={[styles.nameInput]}
                            label="Meal Name"
                            value={text}
                            onChangeText={text => setText(text)}
                        />
                        <TextInput 
                            mode='flat'
                            style={[styles.macroInput]}
                            label="Weight (g)"
                            value={text}
                            onChangeText={text => setText(text)}
                        />
                        </View>
                        <View style={{flex: 1, flexDirection: 'row'}}>
                            <TextInput 
                                mode='flat'
                                style={[styles.macroInput]}
                                label="Carbs  (per 100g)"
                                value={text}
                                onChangeText={text => setText(text)}
                                right={<TextInput.Affix text="/100g" />}
                            />
                            <TextInput 
                                mode='flat'
                                style={[styles.macroInput]}
                                label="Fat  (per 100g)"
                                value={text}
                                onChangeText={text => setText(text)}
                                right={<TextInput.Affix text="/100g" />}
                            />
                            <TextInput 
                                mode='flat'
                                style={[styles.macroInput]}
                                label="Protein  (per 100g)"
                                value={text}
                                onChangeText={text => setText(text)}
                                right={<TextInput.Affix text="/100g" />}
                            />
                        </View>
                    </View>
                    <View style={{ flexDirection:'row' ,justifyContent:'center', marginTop:60}}>
                        <View style={styles.calorieCount}>
                            <Text>Calories in the Meal</Text>
                            <Text> 231 kcal</Text>
                        </View>
                        <Button style={styles.flexItem} mode='contained'>Scan</Button>
                        <Button style={styles.flexItem} mode='contained'>Add Meal</Button>
                    </View>
                </View>
            </View>
        </Modal>
    )
}

const styles = StyleSheet.create({
    container: {
        flex:1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'rgba(0,0,0,0.5)',
    },
    card: {
        display:'flex',
        justifyContent:'center',
        alignItems: 'center',
        backgroundColor: 'white',
        padding: 20,
        borderRadius: 5,
    },
    nameInput: {
        margin: 12,
        color: 'darkviolet',
        backgroundColor: 'white',
        width: 400,
    },
    weightInput: {
        margin: 12,
        color: 'darkviolet',
        backgroundColor: 'white',
        width: 200,
    },
    macroInput: {
        margin: 12,
        color: 'darkviolet',
        backgroundColor: 'white',
        width: 190,
    },
    flexItem: {
        marginLeft: 20,
    },
    calorieCount: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        marginRight: 100,
    }
})


export default AddMealComponent
