import React, { useState, useEffect } from 'react'
import { View, StyleSheet, Modal, TextInput } from 'react-native'
import { Button, IconButton, Text, Divider } from 'react-native-paper';
import * as SecureStore from 'expo-secure-store'

import BarCodeScannerComponent from './barcodescanner';
import MealSearchBarComponent from './mealsearchbar';

import { Meal } from '../screens/homescreen';


interface Props {
    setShowAddMeal: React.Dispatch<React.SetStateAction<boolean>>,
    setMeals: React.Dispatch<React.SetStateAction<Meal[]>>,
    meals: Meal[],
    meal: Meal,
    setMeal: React.Dispatch<React.SetStateAction<Meal>>,
    updateDiary: (passedMeals?: Meal[], passedNote?: string) => void,
}

const AddMealComponent: React.FC<Props> = (
    {
        setShowAddMeal, 
        setMeals, 
        meals, 
        meal, 
        setMeal, 
        updateDiary
    }) => {
    const [showBarCodeScanner, setShowBarCodeScanner] = useState<boolean>(false);


    const updateMeals = () => {
        let newMeals:Meal[] = [...meals, meal];
        updateDiary(newMeals);
    }
    const countCalories = () => {
        return Math.round(meal.weight / 100 * (meal.carbs * 4 + meal.fat * 9 + meal.protein * 4))
    }
    const addMealToLocalStorage = async () => {
        const localStorageMeals = await SecureStore.getItemAsync('meals');
        console.log(localStorageMeals);
        if(!localStorageMeals){
            SecureStore.setItemAsync('meals', '[]')
            return
        }
        const localStorageMealsParsed:Meal[] = JSON.parse(localStorageMeals);
        const mealInStorage = localStorageMealsParsed.filter(element => element.name == meal.name);
        if(mealInStorage[0]){
            return
        }
        SecureStore.setItemAsync('meals', JSON.stringify([...localStorageMealsParsed, meal]));
    }

    return(
        <Modal transparent visible={true}>
            <View style={styles.container}>
                <View style={styles.card}>
                    <IconButton 
                        style={{alignSelf:'flex-end'}}
                        icon='window-close' 
                        onPress={() => {
                            setMeal({
                                name: '',
                                weight: 0,
                                carbs: 0,
                                fat: 0,
                                protein: 0,
                            });
                            setShowAddMeal(false);
                        }}
                    />

                        <View style={{display:'flex', flexDirection: 'row',}}>
                            <MealSearchBarComponent 
                                meal={meal}
                                setMeal={setMeal}
                            />
                        </View>

                        <View style={{alignSelf:'stretch', flexDirection: 'row',justifyContent:'space-between'}}>
                        <Text style={styles.inputDescription}>Weight(g)</Text>
                        <TextInput
                            style={styles.input}
                            onChangeText={(input) => setMeal({...meal, weight: Number(input)})}
                            value={String(meal.weight)}
                            placeholder='Weight (g)'
                            keyboardType='numeric'
                        />
                        </View>
                        <View style={{alignSelf:'stretch', flexDirection: 'row',justifyContent:'space-between'}}>
                        <Text style={styles.inputDescription}>Carbs(g per 100g)</Text>
                        <TextInput
                            style={styles.input}
                            onChangeText={(input) => setMeal({...meal, carbs: Number(input)})}
                            value={String(meal.carbs)}
                            placeholder='Carbs (g per 100g)'
                            keyboardType='numeric'
                        />
                        </View>
                        <View style={{alignSelf:'stretch', flexDirection: 'row',justifyContent:'space-between'}}>
                        <Text style={styles.inputDescription}>Fat(g per 100g)</Text>
                        <TextInput
                            style={styles.input}
                            onChangeText={(input) => setMeal({...meal, fat: Number(input)})}
                            value={String(meal.fat)}
                            placeholder='Fat (g per 100g)'
                            keyboardType='numeric'
                        />
                        </View>
                        <View style={{alignSelf:'stretch', flexDirection: 'row',justifyContent:'space-between'}}>
                        <Text style={styles.inputDescription}>Protein(g per 100g)</Text>
                        <TextInput
                            style={styles.input}
                            onChangeText={(input) => setMeal({...meal, protein: Number(input)})}
                            value={String(meal.protein)}
                            placeholder='Protein (g per 100g)'
                            keyboardType='numeric'
                        />
                        </View>

                    <View style={{ flexDirection:'row' ,justifyContent:'center', marginTop:60}}>
                        <View style={styles.calorieCount}>
                            <Text style={{ fontWeight:'bold' }}>Calories in the meal</Text>
                            <Text>{countCalories()}</Text>
                        </View>
                        <Button 
                            style={styles.flexBttn} 
                            color='darkviolet' 
                            mode='contained'
                            onPress={() => setShowBarCodeScanner(true)}
                            >
                                Scan
                        </Button>
                        <Button 
                            style={styles.flexBttn} 
                            color='darkviolet' 
                            mode='contained'
                            onPress={() => {
                                if(meal.weight == 0){
                                    alert(`Please, set value for food weight.`);
                                    return
                                }
                                if(meal.name.length === 0){
                                    alert(`Please, fill in meal name in order to add meal`);
                                    return
                                }
                                addMealToLocalStorage();
                                updateMeals();
                                setMeal({
                                    name: '',
                                    weight: 0,
                                    carbs: 0,
                                    fat: 0,
                                    protein: 0,
                                });
                                setShowAddMeal(false);
                            }}
                            >
                                Add Meal
                        </Button>
                    </View>
                </View>
            </View>
            {showBarCodeScanner && 
                    <Modal>
                        <BarCodeScannerComponent 
                            setMeal={setMeal}
                            setShowBarCodeScanner={setShowBarCodeScanner}
                            />
                    </Modal>}
        </Modal>
    )
}

const styles = StyleSheet.create({
    container: {
        flex:1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'rgba(0,0,0,0.5)',
    },
    card: {
        display:'flex',
        justifyContent:'center',
        alignItems: 'center',
        backgroundColor: 'white',
        padding: 20,
        borderRadius: 5,
    },
    input: {
        height: 40,
        width: 50,
        margin: 12,
        borderBottomWidth: 1.3,
        borderColor:'darkviolet',
        padding: 10,
    },
    nameInput: {
        height: 40,
        width: 300,
        margin: 12,
        borderBottomWidth: 1.3,
        borderColor:'darkviolet',
        padding: 10,
    },
    inputDescription: {
        alignSelf:'flex-end',
        margin:12, 
        fontSize:23,
    },
    flexBttn: {
        marginRight: 20,
    },
    calorieCount: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        marginHorizontal:10,
    }
})


export default AddMealComponent
